const readline=require("readline");const GradingScale=require("../lib/gradingScale");const g=new GradingScale;const rl=readline.createInterface({input:process.stdin,output:process.stdout});rl.on("line",input=>{const cG=parseInt(input);if(cG>=0){g.addPoints(cG);console.log("The corresponding letter grade is "+g.pointsToLetter(cG)+"\n")}else{console.log("The average of your grades is "+g.getAverage()+"\n");g.reset();bye()}});rl.on("close",()=>bye());function bye(){console.log("\nThanks for using the Grading Scale Calculator!");process.exit(0)}console.log("Welcome to the Grading Scale Calculator\n");console.log("Enter your grade and hit enter. When you're done, enter a negative number.\n");
